INSERT INTO `dishes` (`id`, `description`, `image`, `name`, `price`, `category_id`) VALUES ('1', 'Піца Пепероні – це класична італійська піца з томатним соусом, сиром моцарела та ковбасою пепероні.', '/static/images/pepperoni.jpg', 'Піца Пепероні', '259', '1');
INSERT INTO `dishes` (`id`, `description`, `image`, `name`, `price`, `category_id`) VALUES ('2', 'Фріттата - італійський омлет, який готують з начинками з сиру, овочів, ковбаси або м’яса. Зазвичай фритату готують на плиті, потім доводять до готовності в духовці. Неаполітанську фритату часто готують з використанням макаронів.', '/static/images/frittata.jpg', 'Омлет Фріттата', '148', '1');
INSERT INTO `dishes` (`id`, `description`, `image`, `name`, `price`, `category_id`) VALUES ('3', 'Надзвичайно смачна запіканка, з класичною начинкою (томатний соус, м’ясо, моцарела, пармезан та соус бешамель), родом з Емілія-Романья. Тому, якщо колись будете в Болоньї, обов’язково її там скуштуйте.', '/static/images/Lasagna.jpg', 'Лазанья', '300', '1');
INSERT INTO `dishes` (`id`, `description`, `image`, `name`, `price`, `category_id`) VALUES ('4', 'Це вино можна назвати візитною карткою Італії, оскільки про нього знають далеко за межами рідної країни. Не можна сказати, що це вино має якісь визначні характеристики, швидше, воно має дуже універсальний і гармонійний смак, який подобається всім.', '/static/images/prosecco.jpg', 'Просекко', '535', '2');
INSERT INTO `dishes` (`id`, `description`, `image`, `name`, `price`, `category_id`) VALUES ('5', 'Це вино отримало свою назву на честь тієї місцевості, де її виробляють. Наразі використання цього імені дозволено лише у межах певної території, і це суворо контролюється на законодавчому рівні.', '/static/images/asti.jpg', 'Асті', '600', '2');
INSERT INTO `dishes` (`id`, `description`, `image`, `name`, `price`, `category_id`) VALUES ('6', 'Легке ігристе вино їх категорії мас-маркету, хоча зрідка можна знайти і справді якісні вишукані вина цієї категорії. Напій має дуже відомий фруктово-ягідний смак і стане ідеальним доповненням для гарячих літніх вечорів.', '/static/images/lambrusco.jpg', 'Ламбруско', '1090', '2');
INSERT INTO `dishes` (`id`, `description`, `image`, `name`, `price`, `category_id`) VALUES ('7', 'Салат в патріотичних кольорах, який символізує прапор Італії. Був названий в честь відомого острова Капрі, тому, думаю, не варто казати де його готують найсмачніше.', '/static/images/сaprese.jpg', 'Капрезе', '135', '1');
INSERT INTO `dishes` (`id`, `description`, `image`, `name`, `price`, `category_id`) VALUES ('8', 'Легкі закуски, з хліба “чіабата”, які подаються перед основним прийомом їжі. Це гарячі бутерброди, адже основа з хліба підсушена на грилі, натерта часником та оливковою олією.', '/static/images/bruschetta.jpg', 'Брускета', '85', '1');
INSERT INTO `dishes` (`id`, `description`, `image`, `name`, `price`, `category_id`) VALUES ('9', 'Такі ненависні галушки з дитинства, але коли Ви скуштуєте їх в Італії, то зрозумієте, скільки втратили. Адже тут їх готують з манної крупи, кукурудзяного борошна, картоплі.', '/static/images/gnocchi.jpg', 'Ньоккі', '170', '1');
